import './postItem.css'
import FavoriteIcon from '@mui/icons-material/Favorite';
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import EditIcon from '@mui/icons-material/Edit';

export const PostItem = ({
    title,
    tag,
    body,
    name,
    password,
    liked,
    likeArray,
    likeCounter,
    likePost,
    deletePost,
    handleShowEditForm,
    handleShowDeleteForm,
    handleSelect,
}) =>  {

    const heartFill = liked ? 'crimson' : 'black'

    const showEditForm = () => {
        handleSelect();
        handleShowEditForm();
    }

    const showDeleteForm = () => {
        handleSelect();
        handleShowDeleteForm();
    }

    return (
        <div className="post">
            <div className="postContent">
                <h2>{title}</h2>
                <p>{tag}</p>
                <p>{body}</p>
                <p>@{name}</p>
                <div>
                    <button onClick={likePost}>
                        <FavoriteIcon style={{fill:heartFill}} />
                    </button>
                    <strong>{likeCounter}</strong>
                </div>
            </div>
            <div className="postControl">
                <button className="editBtn" onClick={showEditForm}>
                    <EditIcon/>
                </button>
                <button className="deleteBtn" onClick={showDeleteForm}>
                    <DeleteForeverIcon/>
                </button>
            </div>
        </div>

    );
}
