import './Content.css';
import {PostItem} from "./components/PostItem";
import {Component} from "react";
import {AddPostForm} from "./components/AddPostForm";
import {EditPostForm} from "./components/EditPostForm";
import axios from "axios";
import SearchIcon from '@mui/icons-material/Search';
import {LoginPage} from "./components/LoginPage";


export class Content extends Component {

    state = {
        showAddForm: false,
        showEditForm: false,
        showLoginPage: false,
        blogArr: [],
        selectedPost: {},
        isLoggedIn: (() => {
            return localStorage.getItem('isLoggedIn') === 'true';
        }),
        userName: localStorage.getItem('userName'),
    }

    setUserName = (name) => {
        this.setState({
            userName: name
        })
    }

    likePost = (blogPost) => {

        const temp = {...blogPost};
        temp.liked = !temp.liked

        axios.put(`https://62c570e8a361f7251284731c.mockapi.io/posts/${blogPost.id}`, temp)
            .then((response) => {
                this.getPosts()
            })
            .catch((err) => {
                console.log(err)
            })
    }

    getPosts = () => {
        axios.get('https://62c570e8a361f7251284731c.mockapi.io/posts')
            .then((response) => {
                this.setState({
                    blogArr: response.data
                })
            })
            .catch((err) => {
                console.log(err)
            })
    }

    deletePost = (blogPost) => {
        if (window.confirm(`Удалить пост "${blogPost.title}" ?`)) {

            axios.delete(`https://62c570e8a361f7251284731c.mockapi.io/posts/${blogPost.id}`)
                .then((response) => {
                    this.getPosts()
                })
                .catch((error) => {
                    console.log(error)
                })


        }
    }

    handleShowAddForm = () => {

        this.setState({
            showAddForm: true
        })
        //console.log(this.state.showAddForm)
    }

    handleHideAddForm = () => {

        this.setState({
            showAddForm: false
        })
        //console.log(this.state.showAddForm)
    }

    handleShowEditForm = () => {

        this.setState({
            showEditForm: true
        })
        //console.log(this.state.showAddForm)
    }

    handleHideEditForm = () => {

        this.setState({
            showEditForm: false
        })
        //console.log(this.state.showAddForm)
    }

    handleShowLoginPage = () => {
        this.setState({
            showLoginPage: true
        })
    }

    handleHideLoginPage = () => {

        this.setState({
            showLoginPage: false
        })
        //console.log(this.state.showAddForm)
    }

    handleLogIn = (e) => {
        //console.log(this.state.userName)
        this.setState({
            showLoginPage: false,
            isLoggedIn: true
        })
    }

    handleLogOut = (e) => {
        this.setState({
            isLoggedIn: false
        })
        localStorage.setItem('isLoggedIn', false)
        localStorage.setItem('userName', '')
    }

    handleEscape = (e) => {
        if (e.key === 'Escape' && this.state.showAddForm) {
            this.handleHideAddForm()
        }
        if (e.key === 'Escape' && this.state.showEditForm) {
            this.handleHideEditForm()
        }
        if (e.key === 'Escape' && this.state.showLoginPage) {
            this.handleHideLoginPage()
        }
    }

    componentDidMount() {
        this.getPosts()
        window.addEventListener('keyup', this.handleEscape)
    }

    componentWillUnmount() {
        window.removeEventListener('keyup', this.handleEscape)
    }

    addNewPost = (blogPost) => {
        axios.post('https://62c570e8a361f7251284731c.mockapi.io/posts', blogPost)
            .then((response) => {
                this.getPosts()
            })
            .catch((err) => {
                console.log(err)
            })
    }

    editBlogPost = (updatedBlogPost) => {
        axios.put(`https://62c570e8a361f7251284731c.mockapi.io/posts/${updatedBlogPost.id}`, updatedBlogPost)
            .then((response) => {
                this.getPosts()
            })
            .catch((err) => {
                console.log(err)
            })
    }


    handleSelectPost = (blogPost) => {
        this.setState({
            selectedPost: blogPost
        })
    }

    render() {

        const blogPosts = this.state.blogArr.map((item, position) => {
            return (
                <PostItem
                    key={item.id}
                    title={item.title}
                    tag={item.tag}
                    body={item.body}
                    liked={item.liked}
                    likePost={() => this.likePost(item)}
                    deletePost={() => this.deletePost(item)}
                    handleShowEditForm={this.handleShowEditForm}
                    handleSelect={() => this.handleSelectPost(item)}
                />
            )
        })

        return (

            <div className="blogPage">

                    {
                        this.state.showLoginPage ?
                            <LoginPage
                                isLoggedIn={this.state.isLoggedIn}
                                setUserName={this.setUserName}
                                handleLogIn={this.handleLogIn}
                                handleHideLoginPage={this.handleHideLoginPage}
                            />
                            :
                            null
                    }

                {
                    this.state.showAddForm ?
                        <AddPostForm blogArr={this.state.blogArr}
                                     handleHideAddForm={this.handleHideAddForm}
                                     addNewPost={this.addNewPost}
                        />
                        :
                        null
                }

                {
                    this.state.showEditForm ?
                        <EditPostForm
                            handleHideEditForm={this.handleHideEditForm}
                            selectedPost={this.state.selectedPost}
                            editBlogPost={this.editBlogPost}
                        />
                        :
                        null
                }

                <div className="addNewPost">
                    {
                        !this.state.isLoggedIn ?
                            <div>
                                <button className="blackBtn" onClick={this.handleShowLoginPage}>Войти</button>
                            </div>
                        :
                            <div>
                                <button className="blackBtn" onClick={this.handleLogOut}>{this.state.userName}: Выйти</button>
                            </div>
                    }


                    <form method="get" className="searchTags">
                        <input className="searchTagsInput" placeholder="Найти цель по тегу"/>
                        <button className="searchTagsBtn" type="submit">
                            <SearchIcon/>
                        </button>
                    </form>
                    <div>
                        <button className="blackBtn" onClick={this.handleShowAddForm}>Поделиться опытом</button>
                    </div>
                </div>
                <div className="posts">
                    {blogPosts}
                </div>

            </div>
        )
    }
}
