import './LoginPage.css'
import {Component} from "react";
import CancelIcon from "@mui/icons-material/Cancel";

export class LoginPage extends Component {

    render() {
        return(
            <>
                <form className="loginForm">
                    <button onClick={handleHideAddForm} className="hideBtn"><CancelIcon/></button>
                    <h2>Авторизация</h2>
                    <div>
                        <input className="loginInput" type="text" placeholder="Логин"/>
                    </div>
                    <div>
                        <input className="loginInput" type="password" placeholder="Пароль"/>
                    </div>
                    <button className="">Войти</button>
                </form>

                <div className="overlay"></div>
            </>
        )
    }
}
